mapper.select:
SELECT C.CARD_SEQ ,C.CARD_NAME ,C.CARD_IMAGE ,CC.CARD_COMPANY_NAME ,T.CARD_TYPE_NAME ,#{map.cardMoneyValue} AS CARD_MONEY_VALUE FROM t_card C JOIN (SELECT CARD_COMPANY_SEQ, CARD_COMPANY_NAME FROM t_card_company
WHERE
CARD_COMPANY_NAME = #{li}
) CC ON C.CARD_COMPANY_SEQ = CC.CARD_COMPANY_SEQ JOIN t_card_type T ON C.CARD_TYPE_SEQ = T.CARD_TYPE_SEQ JOIN (SELECT CARD_SEQ, CARD_BRAND FROM t_card
WHERE
CARD_BRAND LIKE CONCAT('%', #{li}, '%')
) C2 ON C2.CARD_SEQ = C.CARD_SEQ
WHERE C.CARD_SEQ = #{key} AND T.CARD_TYPE_NAME IN
#{li}
ORDER BY CARD_MONEY_VALUE DESC

SELECT C.CARD_SEQ ,C.CARD_NAME ,C.CARD_IMAGE ,CC.CARD_COMPANY_NAME ,T.CARD_TYPE_NAME ,#{map.cardMoneyValue} AS CARD_MONEY_VALUE FROM t_card C JOIN (SELECT CARD_COMPANY_SEQ, CARD_COMPANY_NAME FROM t_card_company WHERE
CARD_COMPANY_NAME LIKE CONCAT('%', #{li}, '%')
) CC ON C.CARD_COMPANY_SEQ = CC.CARD_COMPANY_SEQ JOIN t_card_type T ON C.CARD_TYPE_SEQ = T.CARD_TYPE_SEQ JOIN (SELECT CARD_SEQ, CARD_BRAND FROM t_card WHERE
CARD_BRAND LIKE CONCAT('%', #{li}, '%')
) C2 ON C2.CARD_SEQ = C.CARD_SEQ
WHERE C.CARD_SEQ = #{key} AND T.CARD_TYPE_NAME IN
#{li}
ORDER BY CARD_MONEY_VALUE DESC

mapper.update:
UPDATE t_card
SET CARD_COUNT = CARD_COUNT+1
WHERE CARD_SEQ = #{param1}
